var mysql = require('mysql');

var pool = mysql.createPool({
    connectionLimit: 100,
    host: 'localhost',
    user: 'images',
    password: 'images',
    database: 'images',
    debug: false
});

module.exports.listImages = function(callback) {
    pool.getConnection(function(err, connection){
        function releaseConnection() {
            connection.removeListener('error', errListener);
            connection.release();
        }
        function errListener(err) {
            releaseConnection();
            callback({ error: "Error: coudln't fetch image list." })
        }
        if(err) {
            callback({ error: "Error: Couldn't get connection." })
            return;
        } else {
            connection.on('error', errListener);
            connection.query("SELECT * FROM metadata ORDER BY created DESC;", function(err, rows, fields){
                if(err){
                    errListener(err);
                } else {
                    releaseConnection();
                    callback(rows);
                }
            })
        };
    });
}